INSERT INTO `$ff/faq` (`rec-id`, `dat1`, `dat2`, `dat3`, `dat4`, `dat5`, `dat6`, `dat7`, `dat8`, `dat9`, `dat10`, `dat11`, `dat12`, `dat13`, `block-id`, `sort`) VALUES
(1, '2018-10-01', '<p>Подскажите пожалуиста,как сделать что бы прицеп тормозил одновременно с камазом.</p>\r\n', 0, 'Артем', '', '', '', 0, 0, '', '<p>весы смотри. на тягаче и на прицепе должны быть. если на прицепе их нет то на тягаче нужно их подвязать. а по сути прицеп должен брать первым. по книге сначала прицеп потом рулевая ось следом телега.</p>\r\n', '', '', 637, 1),
(2, '2018-10-07', '<p>Камаз евро 2 65115.савок.змеевик зброса воздуха без фильтра,накачивает норму потом чихать начинает гдето от 5 и холоднее.а выше плюс 5 работает нормально.Что может быть.Спасибо.</p>\r\n', 0, 'Фарид', '', '', '', 0, 0, '', '<p>Отверни трубку от компрессора и плесни туда спирту 100 гр Заводи и просуши систему...дергая ручник и нажимай на педаль тормоза несколько раз.Когда просушишь плесни тута-же антифризу 100 гр он смажет все резинки и вперед на работу</p>\r\n', '', 'P3BhZ2U9NTc', 637, 2),
(3, '2018-10-14', '<p>камаз евро 2 65115. пол года щелкает в редукторе в среднем под нагрузкой.на ручник ставишь первую вкючаешь и пытаешся стронуться и звук такой тук-тук было у кого такое...</p>\r\n', 0, 'Ник', '', '', '', 0, 0, '', '<p>Штанги реактивные...балансир..карданы тоже могут тук-тук...если-бы редуктор то он уж давно-бы развалился.</p>\r\n', '', 'P3BhZ2U9NTc', 637, 3),
(4, '2018-11-10', '<p>Сделал капиталку двигателю, камаз турбированный, но сегодня присмотрелся где турбина крепится с трубой глушителя там подтеки масла. Мастер говорит что так и должно быть при маленьких оборотах. Так должно быть? И ещё вопрос после капиталки камаз сильно стал дыметь, правда залил другую солярку то есть не там где обычно заливаю. Это из-за солярки или?</p>\r\n', 0, 'Саша', '', '', '', 0, 0, '', '<p>На холостых об не рекомендуется использовать турбо дизель,масло там быть точно недолжно,поменяй прокладку.Дымит он у тебя или сгорает масло в коллекторах или зажигание неверно выставлено .Смотря каким цветом дым и когда и на каких оборотах дымит?</p>\r\n', '', 'P3BhZ2U9NTc', 637, 4),
(5, '2018-11-17', '<p>Из расширительного бачка идет выбивает антифриз только он пенится как будто с масло вместе,двигатель 400сил камаз</p>\r\n', 0, 'Тим', '', '', '', 0, 0, '', 'скорее всего резинки под гильзами стареют.уровень масла какой?если поднялся то попробуй открутить не до конца пробку поддона,если потечет антифриз то снимай гильзы.', '', 'P3BhZ2U9NTc', 637, 5),
(6, '2018-11-19', 'Подскажите,при включении первой или задней появляется хруст,включаешь третью,и тут же первую и все нормально.Что за херня?', 0, 'Наиль', '', '', '', 0, 0, '', 'Попробуй отрегулировать кулису у меня такое было на 152 коробке.\r\nОтведи рычаг на немного в пассажирскую сторону.', '', 'P3BhZ2U9NTc', 637, 6),
(7, '2018-11-22', 'Подскажите,при включении первой или задней появляется хруст,включаешь третью,и тут же первую и все нормально.Что за херня?', 0, 'Смирнов В.Р.', '', '', '', 0, 0, '', 'Попробуйте отрегулировать кулису у меня такое было на 152 коробке. Отведите рычаг на немного в пассажирскую сторону.', '', 'P3BhZ2U9NTc', 637, 7),
(8, '2018-12-01', 'не могу найти причину,почему топливо уходит за 10 мин. все клапана заменены,в соединениях сухо. сам насос ремонтировать?', 0, 'Павел', '', '', '', 0, 0, '', 'Клапана стоят в тннт 2 штуки,один клапан в тнвд и один в крышки фильтров.Поменяй клапан на тнвд,проверить на язык(создать вакуум).На крышке фтот просто замена но надо смотреть на посадочное место поршенька или шарика.Те что в тннт проверяют визуально часто ломается ножка у клапана они пластиковые', '', 'P3BhZ2U9NTc', 637, 8),
(9, '2018-12-05', 'не могу определить причину неисправности камаз4310 авто стоит и ехать не хочет редуктора проверил перебрал раздатку тоже вскрывал видимых причин не обнаружил может кто сталкивался с таким? что посоветуете?', 0, '', '', '', '', 0, 0, '', 'Проверяй целостность пневмо диафрагм те что на раздатке с левой стороны по ходу движения,верхняя пониженная, нижняя камера повышенная передача.Рукой попробуй включить,толкая шток', '', 'P3BhZ2U9NTc', 637, 9),
(10, '2018-12-09', 'Подскажите пожалуйста, шипит кнопка экстренного растормаживания постоянно. Ставишь на ручник перестает. Поменял кнопку, ручник, как устранить.', 0, 'Иван М.', '', '', '', 0, 0, '', 'К ускорительному крану прикручен маленький кран,в нем резиновый пятак запал. разбери почисти. или открути трубку (гайка на 17) и заглуши этот выход на кране. На новых камазах нет растормаживания этого.', '', 'P3BhZ2U9NTc', 637, 10),
(11, '2018-12-11', 'Камаз 2013 года марки 596015 на шасси Кама 343118 мощность двигателя, кВ/л. с. 180.2/245 в начале июня поменял прокладку под 4 целиндром ибо масло капало не по детски прошло 2,5 месяца и всё вернулось масло капает подскажите в чем причина её просто рвёт.! Заранее спасибо забыл написать выросло давление масло в двс показывало 4 сейчас упало', 0, 'Еремеев М.', '', '', '', 0, 0, '', 'Возможно газами рвёт.В каком месте прокладки рвёт?Давление 4 на каких оборотах и при какой температуре двс ?', '', 'P3BhZ2U9NTc', 637, 11),
(12, '2018-12-17', 'Сняли ком все в порядке воздухом все равно не включает где манжет стоит пружина убрали ее все равно не включает даже на прямую от ресивера уже не знаю что делать. Подскажите пожалуйста', 0, 'Данил', '', '', '', 0, 0, '', 'Значит вал не ходит вперед назад на котором шестерня,ищи причину почему вал не ходит,он должен от руки ходить туда сюда!', '', 'P3BhZ2U9NTc', 637, 12);

